def levy(d):
    dim = len(d)
    a = 0.0
    for i in range(dim - 1):
        a += pow((d[i] - 1) / 4.0, 2) * (1 + 10 *
                                         pow(sin(pi * ((d[i] - 1) / 4.0 + 1) + 1), 2))
    b = 1 + (d[0] - 1) / 4
    z = pow(sin(2 * pi * (1 + (d[dim - 1] - 1) / 4)), 2)
    c = pow(sin(pi * b), 2) + a + pow((d[dim - 1] - 1) / 4, 2) * (1 + 10 * z)
    return c


def sumsquare(d):
    s = 0
    for i in range(len(d)):
        s += (i + 1) * d[i]**2
    return s


def zakharov(d):
    dim = len(d)
    a = sum(d**2)
    b = 0
    for i in range(dim):
        b += 0.5 * (i + 1) * d[i]
    return b**2 + b**4 + a


def quadric(d):
    dim = len(d)
    s = 0
    for i in range(dim):
        s1 = 0
        for j in range(i + 1):
            s1 += d[j]
        s += s1**2
    return s


def wrapperFct(fct, dim=30):
    if not isinstance(dim, type(1)):
        raise TypeError("type error")

    @functools.wraps(fct)
    def func(x):
        x = np.array(x)
        if dim == 10:
            return fct(np.dot(x, matrix10D))
        if dim == 30:
            return fct(np.dot(x, matrix30D))
    return func

